mapper.select:
SELECT 

FROM daishou_jilu a

WHERE a.id = #{id}

SELECT 

FROM daishou_jilu a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.shouhuoxm = #{shouhuoxm}


		AND a.wuliugs = #{wuliugs.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM daishou_jilu a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE daishou_jilu SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
