mapper.select:
SELECT 

FROM renwu_run a

WHERE a.id = #{id}

SELECT 

FROM renwu_run a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.parent_id = #{parent.id}


		AND a.parent_ids LIKE 
			'%'||#{parentIds}||'%'
			'%'+#{parentIds}+'%'
			concat('%',#{parentIds},'%')

	${sqlMap.dsf}

mapper.update:
UPDATE renwu_run SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE renwu_run SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id} OR parent_ids LIKE '%,'||#{id}||',%'
