mapper.select:
SELECT 

FROM renwu_templ a

WHERE a.id = #{id}

SELECT
FROM renwu_templ a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.parent_id = #{parent.id}
AND a.parent_ids LIKE '%'||#{parentIds}||'%' '%'+#{parentIds}+'%' concat('%',#{parentIds},'%')
AND a.mingcheng LIKE '%'||#{mingcheng}||'%' '%'+#{mingcheng}+'%' concat('%',#{mingcheng},'%')
AND a.taskdep = #{taskdep.id}

mapper.update:
UPDATE renwu_templ SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE renwu_templ SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id} OR parent_ids LIKE '%,'||#{id}||',%'
