mapper.select:
SELECT 

FROM dict_renyuan a

WHERE a.id = #{id}

SELECT 

FROM dict_renyuan a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.xingming = #{xingming}


		AND a.renyuanlx = #{renyuanlx}


		AND a.suishendh = #{suishendh}


		AND (a.pids LIKE 
			'%'||#{pids}||'%'
			'%'+#{pids}+'%'
			concat('%',#{pids},'%')
		OR a.id=#{id})


		AND a.pid = #{pid}


		AND a.heimingd = #{heimingd}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM dict_renyuan a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE dict_renyuan SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE dict_renyuan SET
pid='',del_flag=1
WHERE id=#{renyuanId}

UPDATE dict_renyuan SET
	heimingd=1
WHERE id=#{renyuanId}

UPDATE dict_renyuan SET
	heimingd=0
WHERE id=#{renyuanId}
