mapper.select:
SELECT 

FROM wuye_jichu a

WHERE a.id = #{id}

SELECT 

FROM wuye_jichu a


	a.del_flag = #{DEL_FLAG_NORMAL} and a.leixingbs=4

		AND a.huxing = #{huxing}


		AND a.xiaoshouzt = #{xiaoshouzt}


		AND a.shiyongzt = #{shiyongzt}


		AND (a.pids LIKE 
			'%'||#{pids}||'%'
			'%'+#{pids}+'%'
			concat('%',#{pids},'%')
		OR a.id=#{id})




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wuye_jichu SET
xiaoshouzt = 1
WHERE id=#{taohuId}

UPDATE wuye_jichu SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id in(#{id})

